import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import './ViewCourses.css'; // External CSS

const ViewCourses = () => {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      const token = localStorage.getItem('token');

      const res = await axios.get('http://localhost:5000/api/courses', {
        headers: { Authorization: `Bearer ${token}` }
      });

      setCourses(res.data);
    } catch (error) {
      console.error(error);
      toast.error(error.response?.data || 'Failed to fetch courses!', { position: 'top-center' });
    }
  };

  return (
    <div className="view-courses-container">
      <h2 className="view-courses-title">ðŸ“š All Available Courses</h2>
      
      <div className="courses-grid">
        {courses.length === 0 ? (
          <p>No courses found!</p>
        ) : (
          courses.map((course) => (
            <div className="course-card" key={course._id}>
              {course.imageLink && (
                <img src={course.imageLink} alt="Course" className="course-image" />
              )}
              <h3>{course.courseName}</h3>
              <p><strong>Code:</strong> {course.courseCode}</p>
              <p>{course.description}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ViewCourses;
