import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import './AdminStats.css'; // External CSS

const AdminStats = () => {
  const [stats, setStats] = useState({
    totalCourses: 0,
    totalBooks: 0,
  });

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const token = localStorage.getItem('token');

      // Fetch Courses Stats
      const courseRes = await axios.get('http://localhost:5000/api/courses/admin/stats', {
        headers: { Authorization: `Bearer ${token}` }
      });

      // For now, just simulate Books Stats
      const booksRes = { data: { totalBooks: 20 } }; // Simulated response for books (You can later create `/api/books/admin/stats`)

      setStats({
        totalCourses: courseRes.data.totalCourses,
        totalBooks: booksRes.data.totalBooks,
      });

    } catch (error) {
      console.error(error);
      toast.error('Failed to fetch dashboard stats!', { position: 'top-center' });
    }
  };

  return (
    <div className="admin-stats-container">
      <h2 className="admin-stats-title">ðŸ“Š Admin Dashboard Statistics</h2>

      <div className="stats-grid">
        <div className="stat-card">
          <h3>Total Courses</h3>
          <p>{stats.totalCourses}</p>
        </div>

        <div className="stat-card">
          <h3>Total Books</h3>
          <p>{stats.totalBooks}</p>
        </div>
      </div>
    </div>
  );
};

export default AdminStats;
