import React from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom'; // âœ… Add useLocation
import './Navbar.css';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation(); // âœ… Detect current route

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('username');
    localStorage.removeItem('role');
    navigate('/');
  };

  const token = localStorage.getItem('token');

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        ðŸ“š <span>Course Resource Library</span>
      </div>
      <div className="navbar-links">
        {!token ? (
          <>
            {/* âœ… Hide Login button if already on Login page */}
            {location.pathname !== "/" && (
              <Link to="/">Login</Link>
            )}
            {/* âœ… Hide Signup button if already on Signup page */}
            {location.pathname !== "/signup" && (
              <Link to="/signup">Signup</Link>
            )}
          </>
        ) : (
          <>
            <button onClick={handleLogout} className="logout-button">Logout</button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
